import "./Portal.css";
export default function Portal() {
  const navigate = useNavigate();
  const isAuthenticated = Boolean(user?.email);

  const handleOnLogout = () => {
    setAppState({});
    navigate("/");
  };

  const title = isAuthenticated
    ? "Appointment Confirmed"
    : "Please login to the portal to see your appointment.";

  const content = isAuthenticated ? (
    <>
      <p className="appt">
        Your appointment is on {moment().calendar(new Date(user.date))}
      </p>
      <p className="location">
        Please head to <strong>{user.location}</strong> on that day.
      </p>
    </>
  ) : (
    <p className="appt">Thank you!</p>
  );

  const button = isAuthenticated ? (
    <button className="btn primary" onClick={handleOnLogout}>
      Logout
    </button>
  ) : (
    <Link to="/login">
      <button className="btn primary">Login</button>
    </Link>
  );

  return (
    <>
      <div className="banner">
        <h1> Activity Feed</h1>

        <div className="buttons">
          <button className="button"> Add exercise</button>
          <button className="button"> Log Sleep</button>
          <button className="button">Record Nutrition</button>
        </div>
      </div>
    </>
  );
}
