import "./Exercise.css"
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import ExerciseGrid from "../ExerciseGrid/ExerciseGrid";
import axios from "axios";

export default function Exercise({user, setUser}){

    const [products, setProducts] = useState()
    const [isFetching, setIsFetching] = useState(false)
    const [error, setError] = useState({})

    useEffect(async () => {

          try {
            const userId = user.id
            const url = `http://localhost:3001/exercise` + userId;
            console.log("URLL", url)
            const res = await axios.get(url)
            if (res?.data?.products) {
              setProducts(res.data.products)
              console.log("Products", products)
            } else {
              setError("Error fetching products.")
            }
          } catch (err) {
            console.log(err)
            const message = err?.response?.data?.error?.message
            setError(message ?? String(err))
          } finally {
            setIsFetching(false)
          }
        } ,[])



    return (

        <>
        <h1 className="title"> Exercise</h1>
        <div className="boxes">
            <h1 className="overview"> Overview </h1>
            <Link to=  "/exerciseForm"><button className="overview-button">Record Exercise </button></Link>
        </div>
        <ExerciseGrid products = {products} />
        </>
    );
}
