import { useState } from "react";

import "./ExerciseForm.css";

import { Link } from "react-router-dom";

const locationOptions = [
  { key: 1, label: "Endurance", value: "endurance" },
  { key: 2, label: "Strength", value: "strength" },
  { key: 3, label: "Flexibility", value: "flexibility" },
  { key: 4, label: "Balance", value: "balance" },
];

export default function ExerciseForm() {
  const [form, setForm] = useState({
    exercise_name: "",
    category: "",
    date: "",
    duration: "",
    intensity: "",
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  return (
    <div className="Register">
      <div className="media"></div>
      <div className="card">
        <h2>Add Exercise</h2>

        {errors.form && <span className="error">{errors.form}</span>}
        <br />

        <div className="form">
          <div className="input-field">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              name="first_name"
              placeholder="Jane"
              value={form.first_name}
              onChange={handleOnInputChange}
            />
            {errors.first_name && (
              <span className="error">{errors.first_name}</span>
            )}
          </div>

          <div className="input-field">
            <label htmlFor="name">Select a date</label>
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={handleOnInputChange}
            />
            {errors.date && <span className="error">{errors.date}</span>}
          </div>

          <div className="input-field">
            <label htmlFor="name">Category</label>
            <select
              name="location"
              onChange={(event) =>
                setForm((f) => ({ ...f, location: event.target.value }))
              }
            >
              {locationOptions.map((location) => (
                <option key={location.key} value={location.label}>
                  {location.label}
                </option>
              ))}
            </select>
            {errors.location && (
              <span className="error">{errors.location}</span>
            )}
          </div>

          <br />

          <div className="split-inputs">
            <div className="input-field">
              <label htmlFor="name">Duration</label>
              <input
                type="text"
                name="first_name"
                placeholder="Jane"
                value={form.first_name}
                onChange={handleOnInputChange}
              />
              {errors.first_name && (
                <span className="error">{errors.first_name}</span>
              )}
            </div>
            <div className="input-field">
              <label htmlFor="name">Intensity</label>
              <input
                type="text"
                name="last_name"
                placeholder="Doe"
                value={form.last_name}
                onChange={handleOnInputChange}
              />
              {errors.last_name && (
                <span className="error">{errors.last_name}</span>
              )}
            </div>
          </div>

          <button> Save</button>
        </div>
      </div>
    </div>
  );
}
